import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils";
import { User, UserProgress } from "../entities/all";
import PressableButton from "../components/PressableButton";

export default function Home() {
  const [totalCooks, setTotalCooks] = useState(0);

  useEffect(() => {
    const fetchProgress = async () => {
      try {
        const user = await User.me();
        const progressRecords = await UserProgress.filter({ user_email: user.email });
        if (progressRecords.length > 0) {
          const progress = progressRecords[0];
          setTotalCooks(progress.completed_cooks || 0);
        }
      } catch (error) {
        console.log("User not logged in");
      }
    };
    fetchProgress();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-50 to-yellow-50 flex flex-col">
      {/* Main Content */}
      <div className="flex-1 flex flex-col justify-center items-center px-6">
        {/* Header */}
        <header className="text-center mb-8">
          <div className="text-4xl font-bold text-orange-800 mb-2">ricey</div>
          <div className="text-lg text-orange-600 font-medium">your rice guide</div>
        </header>

        {/* Logo Placeholder */}
        <div className="w-32 h-32 bg-orange-200 rounded-full flex items-center justify-center mb-8 shadow-lg">
          <div className="text-6xl">üçö</div>
        </div>

        {/* Main Action Button */}
        <div className="w-full max-w-sm">
          <Link to={createPageUrl("Cooking")}>
            <PressableButton className="w-full bg-orange-600 hover:bg-orange-700 text-white py-4 px-8 rounded-lg shadow-lg transform transition-all duration-200 hover:scale-105">
              <div className="text-lg font-bold">Let's Cook!</div>
            </PressableButton>
          </Link>
        </div>

        {/* Secondary Actions */}
        {totalCooks > 0 && (
          <div className="mt-8 grid grid-cols-3 gap-4 w-full max-w-sm">
            <Link to={createPageUrl("History")}>
              <PressableButton className="w-full bg-white hover:bg-gray-50 text-orange-700 py-3 px-4 rounded-lg shadow border border-orange-200">
                <div className="text-sm font-medium">History</div>
              </PressableButton>
            </Link>

            <Link to={createPageUrl("Achievements")}>
              <PressableButton className="w-full bg-white hover:bg-gray-50 text-orange-700 py-3 px-4 rounded-lg shadow border border-orange-200">
                <div className="text-sm font-medium">Awards</div>
              </PressableButton>
            </Link>

            <Link to={createPageUrl("Guide")}>
              <PressableButton className="w-full bg-white hover:bg-gray-50 text-orange-700 py-3 px-4 rounded-lg shadow border border-orange-200">
                <div className="text-sm font-medium">Guide</div>
              </PressableButton>
            </Link>
          </div>
        )}

        {/* Stats */}
        {totalCooks > 0 && (
          <div className="mt-6 text-center">
            <div className="text-sm text-orange-600 opacity-80">
              {totalCooks} successful cooks completed
            </div>
          </div>
        )}
      </div>

      {/* Bottom Logo */}
      <div className="flex justify-center pb-8">
        <div className="w-16 h-16 bg-orange-200 rounded-full flex items-center justify-center shadow">
          <div className="text-2xl">üçö</div>
        </div>
      </div>
    </div>
  );
}
