import React from 'react';
import './RiceLogos.css';

interface RiceLogoProps {
  type: string;
  size?: 'small' | 'medium' | 'large';
}

const RiceLogo: React.FC<RiceLogoProps> = ({ type, size = 'medium' }) => {
  const getLogoClass = () => {
    switch (type.toLowerCase()) {
      case 'jasmine':
        return 'jasmine-logo';
      case 'basmati':
        return 'basmati-logo';
      case 'arborio':
        return 'arborio-logo';
      case 'short_grain':
      case 'short':
        return 'short-logo';
      case 'brown':
        return 'brown-logo';
      case 'sushi':
      case 'wild':
        return 'sushi-logo';
      default:
        return 'jasmine-logo'; // default fallback
    }
  };

  const getSizeClass = () => {
    switch (size) {
      case 'small':
        return 'w-8 h-8';
      case 'large':
        return 'w-16 h-16';
      default:
        return 'w-12 h-12';
    }
  };

  return (
    <div className={`${getLogoClass()} ${getSizeClass()}`} />
  );
};

export default RiceLogo;
