import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "@/utils";
import { User, UserProgress } from "@/entities/all";
import PressableButton from "../components/PressableButton";

export default function Home() {
  const [mascot, setMascot] = useState("◉_◉");
  const [totalCooks, setTotalCooks] = useState(0);

  useEffect(() => {
    const fetchProgress = async () => {
      try {
        const user = await User.me();
        const progressRecords = await UserProgress.filter({ user_email: user.email });
        if (progressRecords.length > 0) {
          const progress = progressRecords[0];
          setTotalCooks(progress.completed_cooks || 0);
          const unlocked = progress.unlocked_achievements || [];

          if (unlocked.includes("perfectionist")) setMascot("【•】_【•】");
          else if (unlocked.includes("variety_master")) setMascot("(✨.✨)");
          else if (unlocked.includes("ten_sessions")) setMascot("⌐■_■");
          else if (unlocked.includes("five_sessions")) setMascot("(-‿-)");
          else if (unlocked.includes("first_cook")) setMascot("^_^");
        }
      } catch (error) {
        console.log("User not logged in");
      }
    };
    fetchProgress();
  }, []);

  return (
    <div className="pixel-font text-pixel-dark page-content">
      {/* Compact Header */}
      <div className="text-center mb-4">
        <div className="text-sm font-bold mb-1">RICE-O-MATIC</div>
        <div className="text-xs blink-animation opacity-80">● SYSTEM READY</div>
      </div>

      {/* Mascot */}
      <div className="text-center mb-4">
        <div className="text-2xl bounce-animation inline-block mb-2">
          {mascot}
        </div>
        <div className="text-xs font-bold">RICE-KUN</div>
        {totalCooks > 0 && (
          <div className="text-xs opacity-60 mt-1">{totalCooks} COOKS</div>
        )}
      </div>

      {/* Main Action */}
      <div className="mb-4">
        <Link to={createPageUrl("Cooking")}>
          <PressableButton className="w-full h-14">
            <div className="text-xs font-bold">► START COOKING</div>
          </PressableButton>
        </Link>
      </div>

      {/* Secondary Actions */}
      <div className="grid-3 mb-4">
        <Link to={createPageUrl("History")}>
          <PressableButton className="h-14">
            <div className="text-xs">HISTORY</div>
          </PressableButton>
        </Link>

        <Link to={createPageUrl("Achievements")}>
          <PressableButton className="h-14">
            <div className="text-xs">AWARDS</div>
          </PressableButton>
        </Link>

        <Link to={createPageUrl("Guide")}>
          <PressableButton className="h-14">
            <div className="text-xs">GUIDE</div>
          </PressableButton>
        </Link>
      </div>

      {/* Status */}
      <div className="text-center mt-auto">
        <div className="text-xs opacity-60">READY FOR OPERATIONS</div>
      </div>
    </div>
  );
}
