import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils";
import { RiceType, CookingSession } from "../entities/all";
import PressableButton from "../components/PressableButton";
import RiceLogo from "../components/RiceLogo";
import chooseSvg from "../assets/choose.svg";
import backFalseSvg from "../assets/back=false.svg";
import backTrueSvg from "../assets/back=true.svg";
import riceyLogoSvg from "../assets/ricey-logo.svg";
import plusFalseSvg from "../assets/+=false.svg";
import plusTrueSvg from "../assets/+=true.svg";
import minusFalseSvg from "../assets/-=false.svg";
import minusTrueSvg from "../assets/-=true.svg";
import waterNeededSvg from "../assets/water-needed.svg";
import waterNeed2Svg from "../assets/water-need2.svg";
import sharpButtonSvg from "../assets/Sharp Button.svg";
import typeFalseSvg from "../assets/type=false.svg";
import typeTrueSvg from "../assets/type=true.svg";
import jasmineSvg from "../assets/jasmine.svg";
import basmatiSvg from "../assets/basmati.svg";
import sushiSvg from "../assets/sushi.svg";
import arborioSvg from "../assets/arborio.svg";
import wholeSvg from "../assets/whole.svg";
import shortSvg from "../assets/short.svg";
import cookFalseSvg from "../assets/cook=false.svg";
import cookTrueSvg from "../assets/cook=true.svg";
import pauseFalseSvg from "../assets/pause=false.svg";
import playFalseSvg from "../assets/play=false.svg";
import timerSvg from "../assets/timer.svg";
import timerFalseSvg from "../assets/timer=false.svg";
import timerTrueSvg from "../assets/timer=true.svg";
import tipSvg from "../assets/tip.svg";

const riceOptions = [
  { category: "jasmine", variety: "white", name: "Jasmine", time: 12, waterRatio: 1.5, svg: jasmineSvg },
  { category: "basmati", variety: "white", name: "Basmati", time: 15, waterRatio: 2.0, svg: basmatiSvg },
  { category: "sushi", variety: "white", name: "Sushi", time: 20, waterRatio: 1.0, svg: sushiSvg },
  { category: "arborio", variety: "white", name: "Arborio", time: 20, waterRatio: 2.0, svg: arborioSvg },
  { category: "brown", variety: "brown", name: "Whole", time: 30, waterRatio: 2.0, svg: wholeSvg },
  { category: "short_grain", variety: "white", name: "Short", time: 20, waterRatio: 1.25, svg: shortSvg }
];

interface RiceOption {
  category: string;
  variety: string;
  name: string;
  time: number;
  waterRatio: number;
  svg: string;
}

export default function CookingPage() {
  const [stage, setStage] = useState('select_rice');
  const [selectedRice, setSelectedRice] = useState<RiceOption | null>(null);
  const [riceDetails, setRiceDetails] = useState<RiceType | null>(null);
  const [cups, setCups] = useState(1.0);

  const [totalTime, setTotalTime] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const [isBackPressed, setIsBackPressed] = useState(false);
  const [isPlusPressed, setIsPlusPressed] = useState(false);
  const [isMinusPressed, setIsMinusPressed] = useState(false);
  const [isChooseBackPressed, setIsChooseBackPressed] = useState(false);
  const [isButtonPressed, setIsButtonPressed] = useState(false);
  const [currentTip, setCurrentTip] = useState(0);

  // 100 Cooking Tips
  const cookingTips = [
    "Rinse rice until water runs clear to remove excess starch and prevent sticky, gummy texture.",
  "Use a 1:1.5 ratio of rice to water for most white rice varieties for perfect texture.",
  "Let rice rest for 10 minutes after cooking with the lid on to finish steaming.",
  "Add a pinch of salt to cooking water to enhance the rice's natural flavor.",
  "Toast rice in a dry pan for 2-3 minutes before adding liquid for nuttier flavor.",
  "Use coconut milk instead of water for tropical-flavored rice dishes.",
  "Add a bay leaf or cardamom pod while cooking for subtle aromatic flavor.",
  "Never lift the lid during cooking - it releases essential steam and affects texture.",
  "Use chicken or vegetable broth instead of water for more flavorful rice.",
  "Add a tablespoon of butter or oil to prevent rice from sticking together.",
  "For brown rice, use a 1:2.5 ratio of rice to liquid and cook for 45-50 minutes.",
  "Soak brown rice for 30 minutes before cooking to reduce cooking time.",
  "Use the absorption method: bring to boil, then simmer covered on lowest heat.",
  "Add a few drops of lemon juice to keep white rice bright and prevent yellowing.",
  "Cook rice in a heavy-bottomed pot to prevent burning and ensure even heating.",
  "For sticky rice, use glutinous rice and steam it in a bamboo steamer.",
  "Add saffron threads to cooking water for golden color and luxurious flavor.",
  "Use day-old rice for fried rice - fresh rice becomes mushy when stir-fried.",
  "Cook rice pilaf-style by sautéing rice with onions before adding liquid.",
  "Add frozen peas or corn in the last 5 minutes of cooking for color and nutrition.",
  "Use a rice cooker for consistently perfect results every time.",
  "For risotto, use Arborio rice and stir constantly while gradually adding warm broth.",
  "Cook rice in milk with sugar and vanilla for a simple rice pudding base.",
  "Add turmeric while cooking for golden yellow rice with earthy flavor.",
  "Steam rice over boiling water in a steamer basket for individual grain separation.",
  "Cook rice with tea instead of water for unique flavor - try jasmine or green tea.",
  "Add a cinnamon stick while cooking for warm, spiced rice perfect for desserts.",
  "Use the pasta method: cook rice in lots of water, then drain when tender.",
  "For Persian-style rice, create a crispy bottom layer called 'tahdig'.",
  "Add coconut flakes and lime zest for tropical coconut rice.",
  "Cook rice with diced tomatoes and herbs for a Mediterranean-style side dish.",
  "Use a microwave: combine rice and water in microwave-safe bowl, cook on high, then medium power.",
  "Add chopped fresh herbs like cilantro or parsley after cooking for fresh flavor.",
  "Cook rice with bone broth for extra protein and rich, savory flavor.",
  "Use a fine-mesh strainer when rinsing rice to prevent small grains from escaping.",
  "Add chopped nuts like almonds or pine nuts for texture and flavor contrast.",
  "Cook rice with wine instead of part of the water for sophisticated flavor.",
  "Use leftover rice within 3-4 days and reheat thoroughly to 165°F for food safety.",
  "Add dried fruits like raisins or cranberries during cooking for sweet-savory rice.",
  "Season with rice vinegar, sugar, and salt to make sushi rice.",

  // EATING TIPS (41-70)
  "Pair rice with beans to create a complete protein containing all essential amino acids.",
  "Let rice cool slightly before eating to improve digestibility and reduce glycemic impact.",
  "Mix different rice varieties in one meal for varied textures and flavors.",
  "Add healthy fats like avocado or nuts to rice for better nutrient absorption.",
  "Eat brown rice for higher fiber content and more vitamins than white rice.",
  "Practice proper chopstick etiquette: never stick them upright in rice bowl.",
  "Serve rice in small bowls rather than large plates for better portion control.",
  "Eat rice with fermented foods like kimchi for better gut health.",
  "Try eating rice with your hands in traditional style - it's perfectly acceptable in many cultures.",
  "Combine rice with colorful vegetables to create visually appealing and nutritious meals.",
  "Eat rice as breakfast porridge with milk, honey, and fresh fruits.",
  "Use rice as a base for grain bowls topped with proteins and vegetables.",
  "Try rice crackers as a gluten-free snack alternative to wheat crackers.",
  "Eat rice with spicy foods - it helps cool the palate and balance heat.",
  "Incorporate rice into soups and stews for heartier, more filling meals.",
  "Make rice salads with vegetables and dressing for light, refreshing meals.",
  "Use rice noodles as a gluten-free pasta alternative in various dishes.",
  "Eat sushi rice at room temperature for optimal flavor and texture.",
  "Try rice milk as a dairy-free alternative in cereals and beverages.",
  "Use rice flour for gluten-free baking and cooking applications.",
  "Make rice-based desserts like rice pudding or rice ice cream for sweet treats.",
  "Eat wild rice for a nuttier flavor and higher protein content than regular rice.",
  "Try rice cakes as a low-calorie base for various toppings and spreads.",
  "Combine rice with curry dishes for the classic pairing that balances flavors.",
  "Eat rice with pickled vegetables for improved digestion and probiotic benefits.",
  "Use rice as stuffing for vegetables like bell peppers or cabbage leaves.",
  "Try black rice for antioxidants and a dramatic purple color in dishes.",
  "Make rice wraps using rice paper for fresh, light meal options.",
  "Eat rice with seaweed for additional minerals and umami flavor.",
  "Practice mindful eating with rice - chew thoroughly and appreciate the texture.",

  // INTERESTING FACTS (71-100)
  "Rice feeds more than half of the world's population as a primary food source.",
  "There are over 40,000 varieties of rice grown worldwide.",
  "Rice has been cultivated for over 10,000 years, making it one of humanity's oldest crops.",
  "China produces more rice than any other country, followed by India.",
  "Rice is naturally gluten-free, making it safe for people with celiac disease.",
  "The word 'rice' comes from the Old French 'ris,' which came from Italian 'riso.'",
  "Wild rice isn't actually rice - it's a seed from aquatic grass species.",
  "Rice can grow in flooded fields because it has special air channels in its stems.",
  "Throwing rice at weddings symbolizes fertility and prosperity in many cultures.",
  "Red rice gets its color from anthocyanins, the same antioxidants found in berries.",
  "Rice is grown on every continent except Antarctica.",
  "The average person eats about 20% of their daily calories from rice globally.",
  "Sake, the Japanese alcoholic beverage, is made from fermented rice.",
  "Rice hulls are used to make building materials, fuel, and even phone cases.",
  "The longest grain of rice ever recorded was 5.75mm long.",
  "Golden rice is genetically modified to contain beta-carotene for vitamin A deficiency.",
  "Rice paddies produce methane, accounting for about 12% of global methane emissions.",
  "The Japanese word 'gohan' means both 'cooked rice' and 'meal.'",
  "Rice paper is actually made from rice starch and is completely edible.",
  "Basmati rice grains can expand to twice their original size when cooked.",
  "Rice is the most important grain crop in terms of human nutrition and caloric intake.",
  "Ancient Egyptians buried rice with their dead to ensure food in the afterlife.",
  "Rice requires about 2,500 liters of water to produce 1 kilogram of grain.",
  "The term 'rice bowl' refers to countries that depend heavily on rice production.",
  "Purple rice contains more antioxidants than blueberries due to its anthocyanin content.",
  "Rice straw is used to make hats, shoes, and even cars in some countries.",
  "The International Year of Rice was declared in 2004 by the United Nations.",
  "Rice genome was the first crop plant genome to be fully sequenced.",
  "Sticky rice was used as mortar in ancient Chinese architecture and still holds today.",
  "Rice symbolizes life, fertility, and prosperity in many Asian cultures and religions."
  ];

  // Randomize tip every 30 seconds
  useEffect(() => {
    if (stage === 'timer') {
      const tipInterval = setInterval(() => {
        setCurrentTip(Math.floor(Math.random() * cookingTips.length));
      }, 30000);

      return () => clearInterval(tipInterval);
    }
  }, [stage]);

  // Set initial random tip when timer starts
  useEffect(() => {
    if (stage === 'timer') {
      setCurrentTip(Math.floor(Math.random() * cookingTips.length));
    }
  }, [stage]);

  useEffect(() => {
    if (stage !== 'timer') return;
    let interval: NodeJS.Timeout | null = null;
    if (isRunning && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            setIsRunning(false);
            handleTimerComplete();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isRunning, timeRemaining, stage]);

  const handleSelectRice = async (riceOption: RiceOption) => {
    setSelectedRice(riceOption);
    const types = await RiceType.list();
    const details = types.find(r => r.category === riceOption.category && r.variety === riceOption.variety);
    setRiceDetails(details || null);
    setStage('set_amount');
  };

  const handleStartCooking = async () => {
    if (!selectedRice) return;

    const timeInSeconds = selectedRice.time * 60;
    setTotalTime(timeInSeconds);
    setTimeRemaining(timeInSeconds);
    setIsComplete(false);

    await CookingSession.create({
      rice_type: `${selectedRice.variety} ${selectedRice.category}`,
      rice_amount: Math.round(cups * 185),
      water_amount: Math.round(cups * (riceDetails?.water_ratio || 1.5) * 240),
      cooking_time: selectedRice.time,
      status: 'active'
    });

    setStage('timer');
    setIsRunning(true);
  };

  const handleTimerComplete = async () => {
    setIsComplete(true);
    if (navigator.vibrate) navigator.vibrate([200, 100, 200]);
    // Logic to update session and user progress
  };

  const adjustCups = (amount: number) => {
    setCups(prev => Math.max(0.25, Math.min(4.0, Math.round((prev + amount) * 4) / 4)));
  };

  const getServingText = () => {
    if (cups <= 0.75) return "SERVES 1";
    if (cups <= 1.5) return "SERVES 2-3";
    if (cups <= 3.0) return "SERVES 4-6";
    return "SERVES 6+";
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const waterCups = Math.round(cups * (selectedRice?.waterRatio || 1.5) * 10) / 10;
  const waterMl = Math.round(cups * (selectedRice?.waterRatio || 1.5) * 240);
  const progress = totalTime > 0 ? ((totalTime - timeRemaining) / totalTime) * 100 : 0;

  return (
    <div className="min-h-screen bg-[#fcfbf4] font-pixel">
      {/* Stage: Select Rice */}
      {stage === 'select_rice' && (
        <div className="relative w-full h-screen max-w-[375px] mx-auto">
          {/* Background Pattern - Circles 48px */}
          <div className="absolute inset-0 opacity-10">
            <div className="w-full h-full" style={{
              backgroundImage: `url("data:image/png;base64,")`,
              backgroundSize: '48px 48px'
            }}></div>
          </div>

          {/* Header - Frame 12 */}
          <div className="absolute top-8 left-8 right-8 flex justify-between items-center">
            <Link to={createPageUrl("Home")}>
              <div 
                className="w-12 h-12 relative"
                onMouseDown={() => setIsChooseBackPressed(true)}
                onMouseUp={() => setIsChooseBackPressed(false)}
                onMouseLeave={() => setIsChooseBackPressed(false)}
                onTouchStart={() => setIsChooseBackPressed(true)}
                onTouchEnd={() => setIsChooseBackPressed(false)}
              >
                <img 
                  src={isChooseBackPressed ? backTrueSvg : backFalseSvg} 
                  alt="Back" 
                  className="w-full h-full cursor-pointer" 
                />
              </div>
            </Link>
            <div className="w-7 h-11">
              <img src={riceyLogoSvg} alt="Ricey Logo" className="w-full h-full" />
            </div>
          </div>

          {/* Title - choose your rice! */}
          <div className="absolute top-40 left-1/2 transform -translate-x-1/2 text-center">
            <div className="text-3xl font-normal text-black whitespace-nowrap">choose your rice!</div>
          </div>

          {/* Rice Options Grid - Frame 3 */}
          <div className="absolute top-72 left-10 right-10">
            <div className="grid grid-cols-2 gap-3" style={{ width: '296px', height: '246px' }}>
              {riceOptions.map((option, index) => (
                <div key={option.name} className="flex items-center justify-center p-1">
                  <img 
                    src={option.svg} 
                    alt={option.name}
                    onClick={() => handleSelectRice(option)}
                    className="w-[140.57px] h-[58.17px] cursor-pointer hover:opacity-80 transition-opacity"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Stage: Set Amount */}
      {stage === 'set_amount' && selectedRice && (
        <div className="relative w-full h-screen max-w-[375px] mx-auto">
          {/* Background Pattern - Circles 48px */}
          <div className="absolute inset-0 opacity-10">
            <div className="w-full h-full" style={{
              backgroundImage: `url("data:image/png;base64,")`,
              backgroundSize: '48px 48px'
            }}></div>
          </div>

          {/* Header - Frame 12 */}
          <div className="absolute top-8 left-8 right-8 flex justify-between items-center">
            <div
              className="w-12 h-12 relative cursor-pointer"
              onMouseDown={() => setIsBackPressed(true)}
              onMouseUp={() => setIsBackPressed(false)}
              onMouseLeave={() => setIsBackPressed(false)}
              onTouchStart={() => setIsBackPressed(true)}
              onTouchEnd={() => setIsBackPressed(false)}
              onClick={() => setStage('select_rice')}
            >
              <img 
                src={isBackPressed ? backTrueSvg : backFalseSvg} 
                alt="Back" 
                className="w-full h-full" 
              />
            </div>
            <div className="w-7 h-11">
              <img src={riceyLogoSvg} alt="Ricey Logo" className="w-full h-full" />
            </div>
          </div>

          {/* Main Content - Frame 6 */}
          <div className="absolute top-32 left-10 right-10">
            <div className="flex flex-col space-y-8">
              {/* Rice Details - Frame 4 */}
              <div className="w-[205px] h-[84px] flex flex-col space-y-1 mx-auto">
                <div className="text-5xl font-normal text-black text-center">{selectedRice.name}</div>
                <div className="text-xl font-code text-black text-center">{selectedRice.time} minute cooking</div>
              </div>

              {/* Cups Amount - Frame 7 */}
              <div className="w-[293px] h-[130.67px] flex flex-col space-y-1">
                <div className="text-xl text-black text-center">rice amount (cups)</div>

                {/* Amount Controls - Frame 5 */}
                <div className="w-[293px] h-[78.67px] flex items-center space-x-1">
                  {/* Minus Button */}
                  <div className="w-[49px] h-[50px] relative">
                    <div 
                      className="w-full h-full cursor-pointer"
                      onMouseDown={() => setIsMinusPressed(true)}
                      onMouseUp={() => setIsMinusPressed(false)}
                      onMouseLeave={() => setIsMinusPressed(false)}
                      onTouchStart={() => setIsMinusPressed(true)}
                      onTouchEnd={() => setIsMinusPressed(false)}
                      onClick={() => setCups(Math.max(0.5, cups - 0.5))}
                    >
                      <img 
                        src={isMinusPressed ? minusTrueSvg : minusFalseSvg} 
                        alt="-" 
                        className="w-full h-full" 
                      />
                    </div>
                  </div>

                  {/* Amount Display */}
                  <div className="w-[188.82px] h-[78.67px] flex items-center justify-center">
                    <div className="w-[151.05px] h-[62.16px] bg-[#fcfcf4] border-[2.7px] border-black shadow-[-5.4px_5.4px_0px_#000000] flex items-center justify-center">
                      <div className="text-2xl font-code font-normal text-black">{cups.toFixed(1)}</div>
                    </div>
                  </div>

                  {/* Plus Button */}
                  <div className="w-[49px] h-[50px] relative">
                    <div 
                      className="w-full h-full cursor-pointer"
                      onMouseDown={() => setIsPlusPressed(true)}
                      onMouseUp={() => setIsPlusPressed(false)}
                      onMouseLeave={() => setIsPlusPressed(false)}
                      onTouchStart={() => setIsPlusPressed(true)}
                      onTouchEnd={() => setIsPlusPressed(false)}
                      onClick={() => setCups(Math.min(5, cups + 0.5))}
                    >
                      <img 
                        src={isPlusPressed ? plusTrueSvg : plusFalseSvg} 
                        alt="+" 
                        className="w-full h-full" 
                      />
                    </div>
                  </div>
                </div>

                {/* Serves Text */}
                <div className="text-lg text-black text-center">serves {Math.ceil(cups * 2)}-{Math.ceil(cups * 3)}</div>
              </div>

              {/* Water Needed Section */}
              <div className="w-[297px] h-[135px] relative">
                <img 
                  src={waterNeededSvg} 
                  alt="Water Needed Frame" 
                  className="w-full h-full" 
                />
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="w-[197px] h-[63px] relative">
                    <img 
                      src={waterNeed2Svg} 
                      alt="Water Amount" 
                      className="w-full h-full" 
                    />
                    <div className="absolute inset-0 flex items-center justify-center">
                      <div className="text-black font-code text-base">
                        {waterCups} cups ({waterMl}ml)
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Start Cooking Button - Sharp Button */}
              <div className="w-[294.71px] h-[70.14px] mx-auto">
                <div
                  className="w-full h-full relative cursor-pointer"
                  onMouseEnter={() => setIsButtonPressed(true)}
                  onMouseLeave={() => setIsButtonPressed(false)}
                  onTouchStart={() => setIsButtonPressed(true)}
                  onTouchEnd={() => setIsButtonPressed(false)}
                  onClick={handleStartCooking}
                >
                  <img 
                    src={isButtonPressed ? cookTrueSvg : cookFalseSvg} 
                    alt="Start Cooking" 
                    className="w-full h-full" 
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Stage: Timer */}
      {stage === 'timer' && selectedRice && (
        <div className="relative w-full h-screen max-w-[375px] mx-auto">
          {/* Background Pattern - Circles 48px */}
          <div className="absolute inset-0 opacity-10">
            <div className="w-full h-full" style={{
              backgroundImage: `url("data:image/png;base64,")`,
              backgroundSize: '48px 48px'
            }}></div>
          </div>

          {/* Header - Frame 12 */}
          <div className="absolute top-8 left-8 right-8 flex justify-between items-center">
            <div
              className="w-12 h-12 relative cursor-pointer"
              onMouseDown={() => setIsBackPressed(true)}
              onMouseUp={() => setIsBackPressed(false)}
              onMouseLeave={() => setIsBackPressed(false)}
              onTouchStart={() => setIsBackPressed(true)}
              onTouchEnd={() => setIsBackPressed(false)}
              onClick={() => setStage('set_amount')}
            >
              <img 
                src={isBackPressed ? backTrueSvg : backFalseSvg} 
                alt="Back" 
                className="w-full h-full" 
              />
            </div>
            <div className="w-7 h-11">
              <img src={riceyLogoSvg} alt="Ricey Logo" className="w-full h-full" />
            </div>
          </div>

          {/* Main Content - Frame 11 */}
          <div className="absolute top-32 left-10 right-10">
            <div className="flex flex-col space-y-14">
              {/* Title - Frame 4 */}
              <div className="w-[267px] h-[96px] flex flex-col space-y-1 mx-auto">
                <div className="text-4xl font-normal text-black text-center">we're cooking some rice</div>
              </div>

              {/* Timer and Controls - Frame 9 */}
              <div className="w-[224px] h-[219px] flex flex-col space-y-6 mx-auto">
                {/* Timer Display */}
                <div className="w-[224px] h-[103px] flex items-center justify-center">
                  <div className="w-[209px] h-[96px] bg-[#FD0] border-[3.7px] border-black shadow-[-7.5px_7.5px_0px_#000000] flex items-center justify-center">
                    <div className="text-3xl font-code font-normal text-black">{formatTime(timeRemaining)}</div>
                  </div>
                </div>

                {/* Play/Pause Controls */}
                <div className="w-[224px] h-[93px] flex justify-between items-center">
                  {/* Pause Button */}
                  <div className="w-[70px] h-[93px] flex flex-col items-center space-y-2">
                    <div 
                      className="w-[70px] h-[70px] cursor-pointer"
                      onClick={() => setIsRunning(false)}
                    >
                      <img 
                        src={pauseFalseSvg} 
                        alt="Pause" 
                        className="w-full h-full" 
                      />
                    </div>
                    <div className="text-sm text-black text-center">pause</div>
                  </div>

                  {/* Play Button */}
                  <div className="w-[70px] h-[93px] flex flex-col items-center space-y-2">
                    <div 
                      className="w-[70px] h-[70px] cursor-pointer"
                      onClick={() => setIsRunning(true)}
                    >
                      <img 
                        src={playFalseSvg} 
                        alt="Play" 
                        className="w-full h-full" 
                      />
                    </div>
                    <div className="text-sm text-black text-center">play</div>
                  </div>
                </div>
              </div>

              {/* Tip Section - Frame 10 */}
              <div className="w-[293px] h-[162px] flex flex-col space-y-6">
                {/* Tip Button */}
                <div className="w-[141px] h-[50px] mx-auto">
                  <img 
                    src={tipSvg} 
                    alt="Tip" 
                    className="w-full h-full cursor-pointer" 
                  />
                </div>

                {/* Tip Content */}
                <div className="w-[293px] h-[89px] bg-[#F5F5F5] border-2 border-black p-4">
                  <div className="text-sm text-black text-center leading-relaxed">
                    {cookingTips[currentTip]}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
