import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils";
import { User, UserProgress } from "../entities/all";
import PressableButton from "../components/PressableButton";

export default function Home() {
  const [mascot, setMascot] = useState("◉_◉");
  const [totalCooks, setTotalCooks] = useState(0);

  useEffect(() => {
    const fetchProgress = async () => {
      try {
        const user = await User.me();
        const progressRecords = await UserProgress.filter({ user_email: user.email });
        if (progressRecords.length > 0) {
          const progress = progressRecords[0];
          setTotalCooks(progress.completed_cooks || 0);
          const unlocked = progress.unlocked_achievements || [];

          if (unlocked.includes("perfectionist")) setMascot("【•】_【•】");
          else if (unlocked.includes("variety_master")) setMascot("(✨.✨)");
          else if (unlocked.includes("ten_sessions")) setMascot("⌐■_■");
          else if (unlocked.includes("five_sessions")) setMascot("(-‿-)");
          else if (unlocked.includes("first_cook")) setMascot("^_^");
        }
      } catch (error) {
        console.log("User not logged in");
      }
    };
    fetchProgress();
  }, []);

  return (
    <div className="pixel-font text-pixel-dark page-content">
      <h1>RICE COOKER APP</h1>
      <p>Mascot: {mascot}</p>
      <p>Total Cooks: {totalCooks}</p>
      <button>Test Button</button>
    </div>
  );
}
