import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils";
import { RiceType, CookingSession } from "../entities/all";
import PressableButton from "../components/PressableButton";
import RiceLogo from "../components/RiceLogo";
import chooseSvg from "../assets/choose.svg";
import backFalseSvg from "../assets/back=false.svg";
import backTrueSvg from "../assets/back=true.svg";
import riceyLogoSvg from "../assets/ricey-logo.svg";
import plusFalseSvg from "../assets/+=false.svg";
import plusTrueSvg from "../assets/+=true.svg";
import minusFalseSvg from "../assets/-=false.svg";
import minusTrueSvg from "../assets/-=true.svg";
import waterNeedSvg from "../assets/water-need.svg";
import sharpButtonSvg from "../assets/Sharp Button.svg";
import typeFalseSvg from "../assets/type=false.svg";
import typeTrueSvg from "../assets/type=true.svg";

const riceOptions = [
  { category: "jasmine", variety: "white", name: "Jasmine", time: 18 },
  { category: "basmati", variety: "white", name: "Basmati", time: 15 },
  { category: "sushi", variety: "white", name: "Sushi", time: 60 },
  { category: "arborio", variety: "white", name: "Arborio", time: 25 },
  { category: "brown", variety: "brown", name: "Whole", time: 45 },
  { category: "short_grain", variety: "white", name: "Short", time: 20 }
];

interface RiceOption {
  category: string;
  variety: string;
  name: string;
  time: number;
}

export default function CookingPage() {
  const [stage, setStage] = useState('select_rice');
  const [selectedRice, setSelectedRice] = useState<RiceOption | null>(null);
  const [riceDetails, setRiceDetails] = useState<RiceType | null>(null);
  const [cups, setCups] = useState(1.0);

  const [totalTime, setTotalTime] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [isComplete, setIsComplete] = useState(false);
  const [isBackPressed, setIsBackPressed] = useState(false);
  const [isPlusPressed, setIsPlusPressed] = useState(false);
  const [isMinusPressed, setIsMinusPressed] = useState(false);

  useEffect(() => {
    if (stage !== 'timer') return;
    let interval: NodeJS.Timeout | null = null;
    if (isRunning && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining(prev => {
          if (prev <= 1) {
            setIsRunning(false);
            handleTimerComplete();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => {
      if (interval) clearInterval(interval);
    };
  }, [isRunning, timeRemaining, stage]);

  const handleSelectRice = async (riceOption: RiceOption) => {
    setSelectedRice(riceOption);
    const types = await RiceType.list();
    const details = types.find(r => r.category === riceOption.category && r.variety === riceOption.variety);
    setRiceDetails(details || null);
    setStage('set_amount');
  };

  const handleStartCooking = async () => {
    if (!selectedRice) return;

    const timeInSeconds = selectedRice.time * 60;
    setTotalTime(timeInSeconds);
    setTimeRemaining(timeInSeconds);
    setIsComplete(false);

    await CookingSession.create({
      rice_type: `${selectedRice.variety} ${selectedRice.category}`,
      rice_amount: Math.round(cups * 185),
      water_amount: Math.round(cups * (riceDetails?.water_ratio || 1.5) * 240),
      cooking_time: selectedRice.time,
      status: 'active'
    });

    setStage('timer');
    setIsRunning(true);
  };

  const handleTimerComplete = async () => {
    setIsComplete(true);
    if (navigator.vibrate) navigator.vibrate([200, 100, 200]);
    // Logic to update session and user progress
  };

  const adjustCups = (amount: number) => {
    setCups(prev => Math.max(0.25, Math.min(4.0, Math.round((prev + amount) * 4) / 4)));
  };

  const getServingText = () => {
    if (cups <= 0.75) return "SERVES 1";
    if (cups <= 1.5) return "SERVES 2-3";
    if (cups <= 3.0) return "SERVES 4-6";
    return "SERVES 6+";
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const waterCups = Math.round(cups * (riceDetails?.water_ratio || 1.5) * 10) / 10;
  const waterMl = Math.round(cups * (riceDetails?.water_ratio || 1.5) * 240);
  const progress = totalTime > 0 ? ((totalTime - timeRemaining) / totalTime) * 100 : 0;

  return (
    <div className="min-h-screen bg-[#fcfbf4] font-pixel">
      {/* Stage: Select Rice */}
      {stage === 'select_rice' && (
        <div className="relative w-full h-screen max-w-[375px] mx-auto">
          {/* Background Pattern - Circles 48px */}
          <div className="absolute inset-0 opacity-10">
            <div className="w-full h-full" style={{
              backgroundImage: `url("data:image/png;base64,")`,
              backgroundSize: '48px 48px'
            }}></div>
          </div>

          {/* Header - Frame 12 */}
          <div className="absolute top-8 left-8 right-8 flex justify-between items-center">
            <Link to={createPageUrl("Home")}>
              <div className="w-12 h-12 border-2 border-black bg-[#f5d70f] shadow-[-3px_3px_0px_#000000] flex items-center justify-center">
                <div className="text-xs">‚Üê</div>
              </div>
            </Link>
            <div className="w-7 h-11">
              <img src={chooseSvg} alt="Ricey Logo" className="w-full h-full" />
            </div>
          </div>

          {/* Title - choose your rice! */}
          <div className="absolute top-40 left-1/2 transform -translate-x-1/2 text-center">
            <div className="text-3xl font-normal text-black whitespace-nowrap">choose your rice!</div>
          </div>

          {/* Rice Options Grid - Frame 3 */}
          <div className="absolute top-72 left-10 right-10">
            <div className="grid grid-cols-2 gap-3" style={{ width: '296px', height: '246px' }}>
              {riceOptions.map((option, index) => (
                <div key={option.name} className="flex items-center justify-center p-1">
                  <img 
                    src={typeFalseSvg} 
                    alt={option.name}
                    onClick={() => handleSelectRice(option)}
                    className="w-[140.57px] h-[58.17px] cursor-pointer hover:opacity-80 transition-opacity"
                  />
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Stage: Set Amount */}
      {stage === 'set_amount' && (
        <div className="relative w-full h-screen max-w-[375px] mx-auto">
          {/* Background Pattern - Circles 48px */}
          <div className="absolute inset-0 opacity-10">
            <div className="w-full h-full" style={{
              backgroundImage: `url("data:image/png;base64,")`,
              backgroundSize: '48px 48px'
            }}></div>
          </div>

          {/* Header - Frame 12 */}
          <div className="absolute top-8 left-8 right-8 flex justify-between items-center">
            <Link to={createPageUrl("Cooking")}>
              <div 
                className="w-12 h-12 relative"
                onMouseDown={() => setIsBackPressed(true)}
                onMouseUp={() => setIsBackPressed(false)}
                onMouseLeave={() => setIsBackPressed(false)}
                onTouchStart={() => setIsBackPressed(true)}
                onTouchEnd={() => setIsBackPressed(false)}
              >
                <img 
                  src={isBackPressed ? backTrueSvg : backFalseSvg} 
                  alt="Back" 
                  className="w-full h-full cursor-pointer" 
                />
              </div>
            </Link>
            <div className="w-7 h-11">
              <img src={riceyLogoSvg} alt="Ricey Logo" className="w-full h-full" />
            </div>
          </div>

          {/* Main Content - Frame 6 */}
          <div className="absolute top-32 left-10 right-10">
            <div className="flex flex-col space-y-14">
              {/* Rice Details - Frame 4 */}
              <div className="w-[205px] h-[84px] flex flex-col space-y-1">
                <div className="text-4xl font-normal text-black text-center">{selectedRice?.name}</div>
                <div className="text-lg text-black text-center">{selectedRice?.time} minute cooking</div>
              </div>

              {/* Cups Amount - Frame 7 */}
              <div className="w-[293px] h-[130.67px] flex flex-col space-y-2">
                <div className="text-xl text-black text-center">rice amount (cups)</div>

                {/* Amount Controls - Frame 5 */}
                <div className="w-[293px] h-[78.67px] flex items-center space-x-1">
                  {/* Minus Button */}
                  <div className="w-[49px] h-[50px] relative">
                    <div 
                      className="w-full h-full cursor-pointer"
                      onMouseDown={() => setIsMinusPressed(true)}
                      onMouseUp={() => setIsMinusPressed(false)}
                      onMouseLeave={() => setIsMinusPressed(false)}
                      onTouchStart={() => setIsMinusPressed(true)}
                      onTouchEnd={() => setIsMinusPressed(false)}
                      onClick={() => adjustCups(-0.25)}
                    >
                      <img 
                        src={isMinusPressed ? minusTrueSvg : minusFalseSvg} 
                        alt="-" 
                        className="w-full h-full" 
                      />
                    </div>
                  </div>

                  {/* Amount Display */}
                  <div className="w-[188.82px] h-[78.67px] flex items-center justify-center">
                    <div className="w-[151.05px] h-[62.16px] bg-[#fcfcf4] border-[2.7px] border-black shadow-[-5.4px_5.4px_0px_#000000] flex items-center justify-center">
                      <div className="text-lg font-normal text-black">{cups.toFixed(2)}</div>
                    </div>
                  </div>

                  {/* Plus Button */}
                  <div className="w-[49px] h-[50px] relative">
                    <div 
                      className="w-full h-full cursor-pointer"
                      onMouseDown={() => setIsPlusPressed(true)}
                      onMouseUp={() => setIsPlusPressed(false)}
                      onMouseLeave={() => setIsPlusPressed(false)}
                      onTouchStart={() => setIsPlusPressed(true)}
                      onTouchEnd={() => setIsPlusPressed(false)}
                      onClick={() => adjustCups(0.25)}
                    >
                      <img 
                        src={isPlusPressed ? plusTrueSvg : plusFalseSvg} 
                        alt="+" 
                        className="w-full h-full" 
                      />
                    </div>
                  </div>
                </div>

                {/* Serves Text */}
                <div className="text-lg text-black text-center">{getServingText()}</div>
              </div>

              {/* Water Needed - Frame 8 */}
              <div className="w-[293px] h-[130px] bg-[#FD0] bg-opacity-50 flex items-center justify-center">
                <div className="w-[197px] h-[115px] flex flex-col space-y-1.5">
                  <div className="text-xl text-[#4B4B4B] text-center">water needed:</div>
                  <div className="w-[197px] h-[63px] flex items-center justify-center">
                    <div className="w-[192px] h-[58px] bg-[#D2D6D8] border-[2.5px] border-black shadow-[-5px_5px_0px_#000000] flex items-center justify-center">
                      <div className="text-lg text-black">{waterCups} cups ({waterMl}ml)</div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Start Cooking Button - Sharp Button */}
              <div className="w-[268.71px] h-[68.14px] mx-auto">
                <PressableButton 
                  onClick={handleStartCooking}
                  className="w-full h-full bg-[#F30C59] shadow-[-7.4px_7.4px_0px_#000000] hover:shadow-[-4px_4px_0px_#000000] hover:translate-x-[-3px] hover:translate-y-[3px] transition-all duration-150 flex items-center justify-center"
                >
                  <div className="text-xl font-normal text-white">start cooking</div>
                </PressableButton>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Stage: Timer */}
      {stage === 'timer' && (
        <>
          <div className="page-header">
            <div className="w-20"></div> {/* Spacer */}
            <div className="text-sm font-bold">TIMER</div>
            <Link to={createPageUrl("Home")}><PressableButton className="px-2 py-1">QUIT</PressableButton></Link>
          </div>
           <div className="text-center mb-3 flex-shrink-0">
            <div className={`text-2xl mb-1 ${isRunning ? 'bounce-animation' : ''}`}>{isComplete ? "‚úÖ" : isRunning ? "üî•" : "üç≤"}</div>
            <div className="text-xs font-bold">{isComplete ? "DONE!" : isRunning ? "COOKING" : "PAUSED"}</div>
          </div>
          <div className="pixel-border bg-pixel-cream p-3 text-center mb-3 flex-shrink-0">
            <div className="text-2xl font-bold mb-1">{formatTime(timeRemaining)}</div>
            <div className="text-xs">{selectedRice?.name} ‚Ä¢ {selectedRice?.time} MIN</div>
          </div>
          <div className="pixel-border bg-pixel-tan mb-4 h-6 relative overflow-hidden flex-shrink-0">
            <div className="absolute left-0 top-0 h-full bg-pixel-brown transition-all duration-1000" style={{ width: `${progress}%` }}></div>
            <div className="absolute inset-0 flex items-center justify-center text-xs font-bold text-pixel-dark">{Math.round(progress)}%</div>
          </div>
          <div className="mt-auto">
            {!isComplete ? (
              <div className="grid-2 gap-3">
                <PressableButton onClick={() => setIsRunning(!isRunning)} className="py-3 bg-pixel-brown text-pixel-cream"><div className="text-xs font-bold">{isRunning ? "‚è∏ PAUSE" : "‚ñ∂ RESUME"}</div></PressableButton>
                <Link to={createPageUrl("Home")} className="w-full"><PressableButton className="py-3 w-full"><div className="text-xs">‚èπ STOP</div></PressableButton></Link>
              </div>
            ) : (
              <div className="space-y-3">
                 <div className="pixel-border bg-pixel-brown text-pixel-cream p-3 text-center victory-animation">
                  <div className="text-2xl mb-1">üçö</div>
                  <div className="text-xs font-bold mb-1">RICE IS READY!</div>
                  <div className="text-xs">LET REST 5 MINUTES</div>
                </div>
                <Link to={createPageUrl("Home")}><PressableButton className="w-full py-3"><div className="text-xs">üè† DONE</div></PressableButton></Link>
              </div>
            )}
          </div>
        </>
      )}
    </div>
  );
}
