import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { createPageUrl } from "../utils";
import { User, UserProgress } from "../entities/all";
import PressableButton from "../components/PressableButton";

export default function Home() {
  const [totalCooks, setTotalCooks] = useState(0);

  useEffect(() => {
    const fetchProgress = async () => {
      try {
        const user = await User.me();
        const progressRecords = await UserProgress.filter({ user_email: user.email });
        if (progressRecords.length > 0) {
          const progress = progressRecords[0];
          setTotalCooks(progress.completed_cooks || 0);
        }
      } catch (error) {
        console.log("User not logged in");
      }
    };
    fetchProgress();
  }, []);

  return (
    <div className="min-h-screen bg-[#fcfbf4] relative overflow-hidden">
      {/* Background Pattern */}
      <div className="absolute inset-0 opacity-20">
        <div className="w-full h-full" style={{
          backgroundImage: `radial-gradient(circle at 25% 25%, #f3d758 2px, transparent 2px)`,
          backgroundSize: '48px 48px'
        }}></div>
      </div>

      {/* Top Logo */}
      <div className="absolute top-8 right-8 z-10">
        <img src="src/assets/ricey-logo.svg" alt="Ricey Logo" className="w-7 h-11" />
      </div>

      {/* Main Content */}
      <div className="relative z-20 flex flex-col items-center justify-center min-h-screen px-6">
        <div className="w-full max-w-[207px] flex flex-col items-center space-y-16">
          {/* Header */}
          <header className="text-center">
            <div className="text-6xl font-bold text-black mb-2 leading-none">ricey</div>
            <div className="text-lg text-black font-normal">your rice guide</div>
          </header>

          {/* Main Logo */}
          <div className="w-[153px] h-[243px] flex items-center justify-center">
            <img src="src/assets/ricey-logo.svg" alt="Ricey Logo" className="w-full h-full" />
          </div>

          {/* Main Action Button */}
          <div className="w-full">
            <Link to={createPageUrl("Cooking")}>
              <button className="w-full bg-[#f30c59] hover:bg-[#d40a4e] text-white py-4 px-8 shadow-[-6px_6px_0px_#000000] hover:shadow-[-4px_4px_0px_#000000] hover:translate-x-[-2px] hover:translate-y-[2px] transition-all duration-150 font-bold text-lg">
                Let's Cook!
              </button>
            </Link>
          </div>
        </div>

        {/* Secondary Actions - Only show if user has cooked before */}
        {totalCooks > 0 && (
          <div className="mt-8 grid grid-cols-3 gap-4 w-full max-w-sm">
            <Link to={createPageUrl("History")}>
              <button className="w-full bg-white hover:bg-gray-50 text-black py-3 px-4 rounded-lg shadow border border-gray-200 font-medium text-sm">
                History
              </button>
            </Link>

            <Link to={createPageUrl("Achievements")}>
              <button className="w-full bg-white hover:bg-gray-50 text-black py-3 px-4 rounded-lg shadow border border-gray-200 font-medium text-sm">
                Awards
              </button>
            </Link>

            <Link to={createPageUrl("Guide")}>
              <button className="w-full bg-white hover:bg-gray-50 text-black py-3 px-4 rounded-lg shadow border border-gray-200 font-medium text-sm">
                Guide
              </button>
            </Link>
          </div>
        )}

        {/* Stats */}
        {totalCooks > 0 && (
          <div className="mt-6 text-center">
            <div className="text-sm text-black opacity-60">
              {totalCooks} successful cooks completed
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
